css:
.github-IssueishDetailView {
  padding: 36px;
  overflow: auto;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  font-size: 1.166666em;
  line-height: 1.5;
  background-color: #fafafa;
}
.github-IssueishDetailView-container {
  max-width: 48em;
  margin: 0 auto;
}
.github-IssueishDetailView-header {
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  padding: 6px;
  border: 1px solid #ffffff;
  border-radius: 4px;
}
.github-IssueishDetailView-headerGroup {
  display: flex;
  align-items: center;
  margin: 6px;
}
.github-IssueishDetailView-headerGroup.is-fullWidth {
  flex: 1 1 100%;
}
.github-IssueishDetailView-headerBadge {
  margin-right: 12px;
}
.github-IssueishDetailView-headerLink {
  color: #dfe0e1;
}
.github-IssueishDetailView-headerStatus {
  margin-left: 12px;
}
.github-IssueishDetailView-checkoutButton {
  margin-right: 12px;
}
.github-IssueishDetailView-headerRefreshButton {
  color: #dfe0e1;
  cursor: pointer;
}
.github-IssueishDetailView-headerRefreshButton::before {
  display: block;
  width: 16px;
  height: 16px;
  line-height: .9;
  margin-right: 0;
  text-align: center;
}
.github-IssueishDetailView-headerRefreshButton.refreshing::before {
  animation: github-IssueishDetailView-headerRefreshButtonAnimation 2s linear 30;
}
@keyframes github-IssueishDetailView-headerRefreshButtonAnimation {
  100% {
    transform: rotate(360deg);
  }
}
.github-IssueishDetailView-avatar {
  margin-right: 12px;
}
.github-IssueishDetailView-avatarImage {
  width: 32px;
  height: 32px;
  border-radius: 4px;
}
.github-IssueishDetailView-title {
  display: inline-block;
  margin: 0;
  font-size: 1.5em;
  font-weight: 500;
  line-height: 1.3;
  color: #ffffff;
}
.github-IssueishDetailView-reactions {
  padding-top: 36px;
  border-top: 1px solid #ffffff;
}
.github-IssueishDetailView-reactions:empty {
  display: none;
}
.github-IssueishDetailView-reactionsGroup {
  margin-right: 24px;
}
.github-IssueishDetailView-buildStatus {
  margin: 36px 0;
}
.github-IssueishDetailView-buildStatus:empty {
  display: none;
}
.github-IssueishDetailView-container > .github-DotComMarkdownHtml {
  margin: 36px 0;
}
atom-dock .github-IssueishDetailView {
  padding: 12px;
  font-size: 13px;
  background-color: #f0f0f0;
}
atom-dock .github-IssueishDetailView-header {
  margin-bottom: 18px;
  padding: 0;
  border: none;
  border-radius: 0;
}
atom-dock .github-IssueishDetailView-headerGroup {
  margin: 0;
}
atom-dock .github-IssueishDetailView-avatarImage {
  width: 24px;
  height: 24px;
  border-radius: 4px;
}
atom-dock .github-IssueishDetailView-title {
  font-size: 1.25em;
  font-weight: 500;
  line-height: 1.3;
}
atom-dock .github-IssueishDetailView-reactions {
  padding-top: 12px;
}
atom-dock .github-IssueishDetailView-buildStatus {
  margin: 12px 0;
}
atom-dock .github-IssueishDetailView-buildStatus:empty {
  display: none;
}
atom-dock .github-IssueishDetailView .github-PrTimeline {
  margin-top: 12px;
}
atom-dock .github-IssueishDetailView .timeline-item {
  margin: 0;
  padding: 18px 0;
}
atom-dock .github-IssueishDetailView .pre-timeline-item-icon {
  position: relative;
  margin: 0;
}
atom-dock .github-IssueishDetailView .pre-timeline-item-icon:before {
  vertical-align: middle;
}
atom-dock .github-IssueishDetailView-container > .github-DotComMarkdownHtml {
  margin: 12px 0;
}
atom-dock .github-PrStatuses-header,
atom-dock .github-PrStatuses-list-item {
  border-radius: 0;
  padding-left: 0;
  padding-right: 0;
  border-left: none;
  border-right: none;
}
