css:
.notifications-log {
  display: flex;
  flex-direction: column;
  min-width: 200px;
}
.notifications-log header {
  flex: none;
  background-color: #fff;
  border-bottom: 1px solid #ffffff;
}
.notifications-log header .notification-type {
  border: none;
  width: 30px;
  height: 30px;
  margin: 1px;
  padding: 0;
  color: #e2e2e2;
  opacity: .5;
  background: #fff;
}
.notifications-log .notifications-log-items {
  flex: auto;
  list-style: none;
  padding: 0;
  margin: 0;
  overflow: auto;
}
.notifications-log .notifications-log-items .notifications-log-item {
  display: flex;
  box-sizing: content-box;
  border-bottom: 1px solid #ffffff;
  max-height: 30px;
  overflow: hidden;
}
.notifications-log .notifications-log-items .notifications-log-item .notifications-log-notification {
  flex: auto;
  display: flex;
  position: relative;
  padding-left: 30px;
  overflow: hidden;
}
.notifications-log .notifications-log-items .notifications-log-item .notifications-log-notification.icon:before {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  width: 30px;
  height: 100%;
  padding-top: 6px;
  text-align: center;
}
.notifications-log .notifications-log-items .notifications-log-item .notifications-log-notification .message {
  flex: 0 1 auto;
  word-wrap: break-word;
  padding: 6px 12px;
  border-left: 1px solid #ffffff;
}
.notifications-log .notifications-log-items .notifications-log-item .notifications-log-notification .btn-toolbar {
  flex: 1 0 auto;
  display: flex;
  align-items: center;
  white-space: nowrap;
}
.notifications-log .notifications-log-items .notifications-log-item .notifications-log-notification .btn-toolbar:empty {
  display: none;
}
.notifications-log .notifications-log-items .notifications-log-item .notifications-log-notification .btn-toolbar .btn-copy-report {
  vertical-align: middle;
  margin-left: 6px;
}
.notifications-log .notifications-log-items .notifications-log-item .notifications-log-notification .btn-toolbar .btn-copy-report::before {
  margin: 0;
}
.notifications-log .notifications-log-items .notifications-log-item .timestamp {
  flex: 0 0 auto;
  white-space: nowrap;
  text-align: center;
  line-height: 30px;
  padding: 0 12px;
}
.notifications-log .notification-type.fatal.show-type {
  color: red;
  opacity: 1;
}
.notifications-log .hide-fatal li.fatal {
  display: none;
}
.notifications-log .notifications-log-notification.fatal .message {
  color: #ff0000;
}
.notifications-log .notifications-log-notification.fatal.icon:before {
  color: red;
}
.notifications-log .notification-type.error.show-type {
  color: red;
  opacity: 1;
}
.notifications-log .hide-error li.error {
  display: none;
}
.notifications-log .notifications-log-notification.error .message {
  color: #ff0000;
}
.notifications-log .notifications-log-notification.error.icon:before {
  color: red;
}
.notifications-log .notification-type.warning.show-type {
  color: orange;
  opacity: 1;
}
.notifications-log .hide-warning li.warning {
  display: none;
}
.notifications-log .notifications-log-notification.warning .message {
  color: #ffa500;
}
.notifications-log .notifications-log-notification.warning.icon:before {
  color: orange;
}
.notifications-log .notification-type.info.show-type {
  color: blue;
  opacity: 1;
}
.notifications-log .hide-info li.info {
  display: none;
}
.notifications-log .notifications-log-notification.info .message {
  color: #0000ff;
}
.notifications-log .notifications-log-notification.info.icon:before {
  color: blue;
}
.notifications-log .notification-type.success.show-type {
  color: green;
  opacity: 1;
}
.notifications-log .hide-success li.success {
  display: none;
}
.notifications-log .notifications-log-notification.success .message {
  color: #008000;
}
.notifications-log .notifications-log-notification.success.icon:before {
  color: green;
}
