/* SYNTAX VARIABLES */
/* COLOR PALETTE */
/*
  OPEN COLOR 1.6.2
  by yeun
  github.com/yeun/open-color
*/
.github-FilePatchView {
  display: flex;
  flex-direction: column;
  -webkit-user-select: none;
  cursor: default;
  flex: 1;
  min-width: 0;
}
.github-FilePatchView-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 6px;
  padding-left: 12px;
  border-bottom: 1px solid #363659;
}
.github-FilePatchView-header .btn {
  font-size: .9em;
}
.github-FilePatchView-header .btn.icon-move-up::before,
.github-FilePatchView-header .btn.icon-move-down::before {
  font-size: 1em;
  margin-right: .5em;
}
.github-FilePatchView-title {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  margin-right: 12px;
}
.github-FilePatchView-container {
  flex: 1;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
}
.github-FilePatchView-container .is-blank,
.github-FilePatchView-container .large-file-patch {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  font-size: 1.2em;
  padding: 12px;
}
.github-FilePatchView-container .large-file-patch {
  flex-direction: column;
}
.github-FilePatchView-meta {
  padding: 12px;
  border-bottom: 1px solid #363659;
}
.github-FilePatchView-metaContainer {
  border: 1px solid #363659;
  border-radius: 4px;
}
.github-FilePatchView-metaHeader {
  display: flex;
  align-items: center;
  padding: 12px;
  background-color: #363659;
}
.github-FilePatchView-metaTitle {
  flex: 1;
  margin: 0;
  font-size: 1.25em;
  line-height: 1.5;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.github-FilePatchView-metaControls {
  margin-left: 12px;
}
.github-FilePatchView-metaControls .btn.icon-move-up::before,
.github-FilePatchView-metaControls .btn.icon-move-down::before {
  font-size: 1em;
  margin-right: .5em;
  vertical-align: baseline;
}
.github-FilePatchView-metaDetails {
  padding: 12px;
}
.github-FilePatchView-metaDiff {
  margin: 0 .2em;
  padding: .2em .4em;
  line-height: 1.6;
  border-radius: 4px;
}
.github-FilePatchView-metaDiff--added {
  color: #8ee3b9;
  background-color: #2c494e;
}
.github-FilePatchView-metaDiff--removed {
  color: #ed9797;
  background-color: #4c2f43;
}
.github-FilePatchView-metaDiff--fullWidth {
  display: block;
  margin: 6px 0 0 0;
}
.github-FilePatchView-metaDiff--fullWidth:first-child {
  margin-top: 0;
}
.github-FilePatchView-metaDiff code {
  margin: 0 .2em;
  padding: 0 .2em;
  font-size: .9em;
  color: inherit;
  line-height: 1;
  vertical-align: middle;
  word-break: break-all;
  background-color: #27273f;
}
