css:
.github-RemotePrController {
  flex: 1;
  display: flex;
  flex-direction: column;
}
.github-PrInfo {
  padding: 24px;
  overflow: auto;
  flex: 1;
  cursor: default;
}
.github-PrInfo .icon {
  vertical-align: middle;
}
.github-PrInfo .pinned-by-url {
  cursor: pointer;
}
.github-PrInfo .pinned-pr-info {
  display: flex;
  padding: 12px;
  border: 1px solid #4e4e4e;
  border-radius: 4px;
  margin-bottom: 24px;
}
.github-PrInfo .pinned-pr-info a {
  text-decoration: underline;
}
.github-PrInfo .pr-badge-and-link {
  display: flex;
  align-items: center;
  margin-bottom: 24px;
}
.github-PrInfo .pr-badge-and-link .browser-link {
  display: inline-block;
  margin-left: 0.5em;
}
.github-PrInfo .pr-badge-and-link :last-child {
  flex: 1;
  text-align: right;
}
.github-PrInfo .pr-link a {
  color: #4e4e4e;
  word-break: break-all;
}
.github-PrInfo .refresh-button .icon {
  color: #4e4e4e;
  cursor: pointer;
}
.github-PrInfo .refresh-button .icon.refreshing::before {
  animation: github-RefreshButton-animation 2s linear 30;
}
@keyframes github-RefreshButton-animation {
  100% {
    transform: rotate(360deg);
  }
}
.github-PrInfo .pr-avatar-and-title {
  display: flex;
  align-items: center;
  margin-bottom: 12px;
}
.github-PrInfo .author-avatar-link {
  margin-right: 12px;
  align-self: flex-start;
}
.github-PrInfo .author-avatar {
  max-height: 32px;
  border-radius: 4px;
}
.github-PrInfo .pr-title {
  margin: 0;
  line-height: 1.2;
  color: #ffffff;
}
.github-PrInfo .merge-status,
.github-PrInfo .build-status {
  line-height: 2;
}
.github-PrInfo .merge-status.success .icon {
  color: #61e078;
}
.github-PrInfo .build-status.pending .icon {
  color: #f1ae13;
}
.github-PrInfo .count-number {
  margin-left: .25em;
  font-size: 1.1em;
  font-weight: 600;
  line-height: 1;
}
.github-PrInfo .label {
  padding: 0 6px;
  margin: 0 6px 6px 0;
  border-radius: 4px;
  line-height: 2em;
  display: inline-block;
}
.github-PrInfo .reactions .reaction-group {
  margin-right: 24px;
}
.github-PrInfo .github-PrStatuses {
  border-top: 1px solid #2e2e2e;
  padding-top: 24px;
}
.github-PrInfo .meta-info,
.github-PrInfo .conversation,
.github-PrInfo .commit-count,
.github-PrInfo .file-count,
.github-PrInfo .reactions,
.github-PrInfo .labels {
  border-top: 1px solid #2e2e2e;
  padding: 12px 0;
}
.github-PrInfo .conversation {
  cursor: pointer;
}
