css:
.github-PrPaneItem {
  padding: 36px;
  overflow: auto;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  font-size: 1.25em;
  line-height: 1.5;
  background-color: #1a1a1a;
}
.github-PrPaneItem * {
  user-select: text;
  -webkit-user-select: text;
}
.github-PrPaneItem-container {
  max-width: 48em;
  margin: 0 auto;
}
.github-PrPaneItem .issueish-badge-and-link {
  display: flex;
  align-items: center;
  margin-bottom: 24px;
}
.github-PrPaneItem .issueish-badge-and-link :last-child {
  flex: 1;
  text-align: right;
}
.github-PrPaneItem .issueish-link a {
  color: #4e4e4e;
}
.github-PrPaneItem .pr-build-status-icon {
  margin-left: 12px;
}
.github-PrPaneItem .refresh-button .icon {
  color: #4e4e4e;
  cursor: pointer;
}
.github-PrPaneItem .refresh-button .icon.refreshing::before {
  animation: github-RefreshButton-animation 2s linear 30;
}
@keyframes github-RefreshButton-animation {
  100% {
    transform: rotate(360deg);
  }
}
.github-PrPaneItem .issueish-avatar-and-title {
  display: flex;
  align-items: center;
  margin-bottom: 24px;
  padding-bottom: 12px;
  border-bottom: 1px solid #2e2e2e;
}
.github-PrPaneItem .author-avatar-link {
  margin-right: 12px;
  align-self: flex-start;
}
.github-PrPaneItem .author-avatar {
  max-height: 32px;
  border-radius: 4px;
}
.github-PrPaneItem .issueish-title {
  margin: 0;
  font-size: 1.5em;
  font-weight: 500;
  line-height: 1.3;
  color: #ffffff;
}
.github-PrPaneItem .issueish-body {
  padding-top: 12px;
  border-top: 1px solid #2e2e2e;
}
.github-PrPaneItem .issueish-body > *:first-child {
  margin-top: 0;
}
.github-PrPaneItem .github-DotComMarkdownHtml a {
  color: #77c8f2;
}
.github-PrPaneItem .github-DotComMarkdownHtml p {
  line-height: 1.5;
}
.github-PrPaneItem .github-DotComMarkdownHtml hr {
  margin-top: 5px;
  margin-bottom: 5px;
  border-top: 1px solid #999999;
  width: 100%;
}
.github-PrPaneItem .github-DotComMarkdownHtml ul {
  margin-left: -10px;
}
.github-PrPaneItem .github-DotComMarkdownHtml ol {
  margin-left: -20px;
}
.github-PrPaneItem .github-DotComMarkdownHtml li {
  font-size: .9em;
  padding-top: .1em;
  padding-bottom: .1em;
}
.github-PrPaneItem .github-DotComMarkdownHtml li li {
  font-size: 1em;
}
.github-PrPaneItem .github-DotComMarkdownHtml pre > code {
  white-space: pre;
}
.github-PrPaneItem .reactions {
  width: fit-content;
  padding-top: 12px;
}
.github-PrPaneItem .reactions .reaction-group {
  margin-right: 12px;
  padding: 12px;
  border: 1px solid #2e2e2e;
  border-radius: 4px;
}
.github-PrPaneItem .reactions:empty {
  all: unset;
}
.github-PrPaneItem .github-PrStatuses {
  margin-top: 48px;
  padding: 12px;
  border: 1px solid #2e2e2e;
  border-radius: 4px;
}
.github-PrPaneItem blockquote {
  font-size: inherit;
  padding-top: 0;
  padding-left: 12px;
  padding-bottom: 0;
  color: #4e4e4e;
  border-left: 2px solid #4e4e4e;
}
