css:
.github-PrUrlInputBox-Container {
  display: flex;
  flex: 1;
  flex-direction: row;
}
.github-PrUrlInputBox-Container .github-PrUrlInputBox-Subview {
  padding: 24px;
}
.github-PrUrlInputBox-Container p {
  text-align: center;
}
.github-PrUrlInputBox-pinButton {
  padding: 12px;
  border-bottom: 1px solid #2e2e2e;
  cursor: default;
}
.github-PrUrlInputBox-pinButton + .github-PrUrlInputBox-Subview {
  padding: 12px;
  border-bottom: 1px solid #2e2e2e;
}
.github-PrUrlInputBox-Subview {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-self: center;
  align-items: center;
}
.github-PrUrlInputBox-Subview > button,
.github-PrUrlInputBox-Subview > input {
  margin: 12px;
}
.github-PrUrlInputBox-Subview .icon-git-pull-request:before {
  width: auto;
  font-size: 48px;
  color: #4e4e4e;
}
.github-PrUrlInputBox-Subview p {
  font-size: 1.1em;
  line-height: 1.5;
  -webkit-user-select: none;
  cursor: default;
}
.github-PrUrlInputBox-Subview p:last-of-type {
  margin-bottom: 0;
}
.github-PrUrlInputBox-Subview p a {
  color: #77c8f2;
}
.github-PrUrlInputBox-Subview input[type=text] {
  width: 100%;
}
