css:
.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  text-align: center;
  font-size: 1.25em;
  line-height: 1.4;
  padding: 4em;
  color: #9ea1a8;
  background-color: #282c34;
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .input-checkbox {
  margin-top: -0.2em;
}
.about .group-start {
  margin-top: 4em;
}
.about .group-item {
  margin-top: 1.5em;
}
.about-container {
  width: 100%;
  max-width: 500px;
}
.about-atom-io:hover .about-logo {
  color: #40a977;
}
.about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 1em auto;
  color: #ffffff;
  transition: color 0.2s;
}
.about-version-container:hover {
  color: #c6c8cc;
}
.about-version-container:active {
  color: #efeff0;
}
.about-version {
  margin-right: .5em;
  font-size: 1.25em;
  vertical-align: middle;
}
.about-more-version {
  color: #585c64;
  font-size: .9em;
}
.about-header-release-notes {
  vertical-align: middle;
  margin-left: 1em;
}
.about-updates-box {
  display: flex;
  align-items: center;
  padding: 12px;
  border: 1px solid #3a3f4b;
  border-radius: 8px;
  background-color: #3a3f4b;
}
.about-updates-status {
  flex: 1;
  margin-left: .5em;
  text-align: left;
}
.about-updates-item,
.about-default-update-message .about-updates-label {
  display: block;
}
.about-updates-label {
  color: #585c64;
}
.about-updates-label.is-strong {
  color: #9ea1a8;
}
.about-updates-version {
  margin: 0 .4em;
}
.about-updates-release-notes,
.about-updates-instructions {
  margin: 0 1em 0 1.5em;
}
.about-auto-updates {
  margin-top: 1em;
}
.about-auto-updates input {
  margin-right: .5em;
}
.about-love .icon::before {
  position: relative;
  width: auto;
  height: auto;
  margin-right: 0;
  font-size: 1.5em;
  vertical-align: text-top;
}
.about-love .icon-logo-github::before {
  font-size: 3.6em;
  height: .36em;
}
.about-credits {
  color: #585c64;
}
.about-release-notes {
  color: #33a0ff;
}
.about-release-notes:hover {
  color: #80c3ff;
}
