css:
.github-CoAuthorForm {
  display: flex;
  flex-direction: column;
  margin-top: 12px;
  padding: 6px;
  border-radius: 4px 4px 0 0;
  border: 1px solid #2d3a45;
  border-bottom: 0;
  background-color: #1D252C;
}
.github-CoAuthorForm-row {
  display: flex;
  align-items: center;
  margin: 6px;
}
.github-CoAuthorForm-row.has-buttons {
  margin: 0;
  flex-wrap: wrap;
}
.github-CoAuthorForm-row.has-buttons .btn {
  flex: 1;
  margin: 6px;
}
.github-CoAuthorForm-row.has-buttons .btn-primary {
  flex: 4;
}
.github-CoAuthorForm-label {
  min-width: 6ch;
}
.github-CoAuthorForm + .github-CommitView-coAuthorEditor {
  margin-top: 0;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}
