css:
.github-CoAuthorForm {
  display: flex;
  flex-direction: column;
  margin-top: 16px;
  padding: 8px;
  border-radius: 0px 0px 0 0;
  border: 1px solid #2a2f38;
  border-bottom: 0;
  background-color: #1a1a1a;
}
.github-CoAuthorForm-row {
  display: flex;
  align-items: center;
  margin: 8px;
}
.github-CoAuthorForm-row.has-buttons {
  margin: 0;
  flex-wrap: wrap;
}
.github-CoAuthorForm-row.has-buttons .btn {
  flex: 1;
  margin: 8px;
}
.github-CoAuthorForm-row.has-buttons .btn-primary {
  flex: 4;
}
.github-CoAuthorForm-label {
  min-width: 6ch;
}
.github-CoAuthorForm + .github-CommitView-coAuthorEditor {
  margin-top: 0;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}
