css:
.github-PrStatuses-header {
  display: flex;
  align-items: center;
  border: 1px solid #2a2f38;
  padding: 16px;
  border-radius: 0px 0px 0 0;
}
.github-PrStatuses-donut-chart {
  margin-right: 16px;
}
.github-PrStatuses-donut-chart svg {
  display: block;
  width: 26px;
  height: 26px;
}
.github-PrStatuses-donut-chart svg circle {
  cx: 13px;
  cy: 13px;
  r: 11px;
  stroke-width: 4px;
}
.github-PrStatuses-summary {
  font-weight: bold;
  flex: 1;
}
.github-PrStatuses-list {
  display: flex;
  flex-direction: column;
  list-style: none;
  padding: 0;
  margin-bottom: 0;
}
.github-PrStatuses-list-item {
  display: flex;
  flex-direction: row;
  font-size: .9em;
  padding: 8px 16px;
  border: 1px solid #2a2f38;
  border-top: none;
}
.github-PrStatuses-list-item:last-child {
  border-radius: 0 0 0px 0px;
}
.github-PrStatuses-list-item-icon {
  width: 26px;
  margin-right: 16px;
  text-align: center;
}
.github-PrStatuses-list-item-icon .icon::before {
  margin-right: 0;
}
.github-PrStatuses-list-item-context {
  flex: 1;
  color: #41505e;
}
.github-PrStatuses-list-item-context strong {
  margin-right: .5em;
  color: #b7c5d3;
}
.github-PrStatuses-list-item-details-link {
  margin-left: .5em;
}
.github-PrStatuses-list-item-details-link a {
  color: #539afc;
}
.github-PrStatuses--warning {
  color: #ffd93d;
}
.github-PrStatuses--success {
  color: #28a745;
}
.github-PrStatuses--error {
  color: #dc3545;
}
