css:
.styleguide {
  position: relative;
  display: flex;
  flex-direction: column;
}
.styleguide a {
  text-decoration: underline;
}
.styleguide-controls {
  position: absolute;
  right: 12px;
  top: 12px;
  z-index: 100;
}
.styleguide-header {
  padding: 18px;
  border-bottom: 1px solid #303337;
}
.styleguide-header h1 {
  font-size: 2em;
  margin: 0 0 .5em 0;
  color: #ffffff;
}
.styleguide-header p {
  font-size: 1.2em;
}
.styleguide-header p:last-of-type {
  margin-bottom: 0;
}
.styleguide-sections {
  flex: 1;
  overflow: auto;
}
.styleguide-sections > section {
  background-color: #181a1c;
  padding: 0;
  border-bottom: 1px solid #303337;
  border-top: none;
}
.styleguide-sections > section:last-child {
  margin-bottom: 0;
}
.styleguide-sections > section.collapsed {
  background-color: #1d1f21;
}
.styleguide-sections > section.collapsed > .section-heading {
  display: block;
  margin: 0;
  padding-bottom: 18px;
  color: #a3a5a5;
}
.styleguide-sections > section.collapsed > .section-heading:hover {
  color: #ffffff;
  background-color: #303337;
}
.styleguide-sections > section.collapsed > .section-heading:active {
  background-color: #1d1f21;
}
.styleguide-sections > section.collapsed > * {
  display: none;
}
.styleguide-sections .section-heading.section-heading {
  padding: 18px 18px 0 18px;
  cursor: pointer;
  font-weight: normal;
  font-size: 1.8em;
  color: #ffffff;
}
.styleguide-sections section > h2 {
  font-size: 1.5em;
  line-height: 1.2;
  margin: 1em 18px 0 18px;
  color: #ffffff;
}
.styleguide-sections section > p {
  font-size: 1.1em;
  margin: 0.5em 18px 1em 18px;
}
.styleguide .example {
  display: flex;
  flex-wrap: wrap;
  border-radius: 4px;
  padding: 6px;
}
.styleguide .example .example-rendered,
.styleguide .example .example-code {
  position: relative;
  flex: 1 1 300px;
  min-width: 0;
  margin: 6px;
  border-radius: 4px;
  border: 1px solid #303337;
}
.styleguide .example .example-rendered {
  padding: 12px;
  background: #1d1f21;
  overflow: hidden;
}
.styleguide .example .example-code {
  background-color: #1d1f21;
}
.styleguide .example .example-code pre {
  border: none;
  background-color: inherit;
}
